CREATE OR REPLACE FUNCTION public.admin_create_employee_full(p_account uuid, p_email text, p_password text)
 RETURNS jsonb
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public', 'auth'
AS $function$
declare
  v_uid     uuid;
  v_service text := private.get_secret('SERVICE_ROLE_KEY');
  v_url     text := private.get_secret('SUPABASE_URL');
  v_resp    http_response;
begin
  if not private.is_sql_console() and not public.is_super_admin() then
    raise exception 'forbidden';
  end if;

  if coalesce(v_service,'') = '' or coalesce(v_url,'') = '' then
    raise exception 'Missing SERVICE_ROLE_KEY or SUPABASE_URL secret';
  end if;

  -- 1) Ù‡Ù„ ÙŠÙˆØ¬Ø¯ Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…ØŸ
  select u.id into v_uid
  from auth.users u
  where lower(u.email) = lower(p_email)
  limit 1;

  -- 1-b) Ø¥Ù† Ù„Ù… ÙŠÙˆØ¬Ø¯: Ø£Ù†Ø´Ø¦Ù‡ Ø¹Ø¨Ø± REST Admin
  if v_uid is null then
    select r.* into v_resp
    from public.http(
      ROW(
        'POST'::text,
        v_url || '/auth/v1/admin/users',
        ARRAY[
          ('apikey',        v_service)::public.http_header,
          ('authorization', 'Bearer '||v_service)::public.http_header
        ]::public.http_header[],
        'application/json'::text,                              -- â‘£ content_type
        json_build_object(                                     -- â‘¤ content ÙƒÙ†Øµ
          'email',         p_email,
          'password',      p_password,
          'email_confirm', true
        )::text
      )::public.http_request
    ) as r;

    if v_resp.status < 200 or v_resp.status >= 300 then
      raise exception 'Auth Admin create user failed: % %', v_resp.status, v_resp.content;
    end if;

    select u.id into v_uid
    from auth.users u
    where lower(u.email) = lower(p_email)
    limit 1;

    if v_uid is null then
      raise exception 'Unable to create/find employee in auth.users';
    end if;
  end if;

  -- 2) Ø±Ø¨Ø·Ù‡ Ø¨Ø§Ù„Ø­Ø³Ø§Ø¨
  perform public.admin_attach_employee(p_account, v_uid);

  -- 3) profile
  insert into public.profiles(id, role, account_id)
  values (v_uid, 'employee', p_account)
  on conflict (id) do update
    set role = excluded.role, account_id = excluded.account_id;

  return jsonb_build_object('ok', true, 'user_uid', v_uid);
end;
$function$

