$function$

CREATE OR REPLACE FUNCTION public.admin_link_member(p_account uuid, p_user_uid uuid, p_role text DEFAULT 'employee'::text)
 RETURNS void
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public', 'auth'
AS $function$
begin
  -- Ù„Ø§Ø¨Ø¯ ÙŠÙƒÙˆÙ† Ø§Ù„Ù…Ù†ÙÙ‘Ø° Ø³ÙˆØ¨Ø± Ø£Ø¯Ù…Ù† Ø£Ùˆ Owner/Admin ÙÙŠ Ù†ÙØ³ Ø§Ù„Ø­Ø³Ø§Ø¨
  if not (
    app.is_super_admin()
    or exists (
      select 1 from public.account_users
      where account_id = p_account
        and user_uid   = app.jwt_uid()
        and disabled   = false
        and role in ('owner','admin')
    )
  ) then
    raise exception 'not allowed';
  end if;

  if p_role not in ('owner','admin','employee') then
    raise exception 'invalid role %', p_role;
  end if;

  if exists (select 1 from public.accounts where id = p_account and frozen) then
    raise exception 'account is frozen';
  end if;

  -- ØªØ£ÙƒØ¯ Ù…Ù† ÙˆØ¬ÙˆØ¯ Ø¨Ø±ÙˆÙØ§ÙŠÙ„
  insert into public.profiles(id) values (p_user_uid)
  on conflict (id) do nothing;

  -- Upsert Ù„Ù„Ø¹Ø¶ÙˆÙŠØ©
  insert into public.account_users(account_id, user_uid, role, disabled)
  values (p_account, p_user_uid, p_role, false)
  on conflict (account_id, user_uid) do update
    set role = excluded.role,
        disabled = false;

  -- Ø­Ø¯Ù‘Ø« Ø§Ù„Ø­Ø³Ø§Ø¨ Ø§Ù„Ø§ÙØªØ±Ø§Ø¶ÙŠ ÙˆØ§Ù„Ø¯ÙˆØ± ÙÙŠ profiles
  update public.profiles
     set account_id = p_account,
         role = p_role
   where id = p_user_uid;
end;
$function$

CREATE OR REPLACE FUNCTION public.admin_link_member_by_email(p_account uuid, p_email text, p_role text DEFAULT 'employee'::text)
 RETURNS void
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public', 'auth'
AS $function$
declare
  v_uid uuid;
begin
  select id into v_uid from auth.users where lower(email) = lower(p_email);
  if v_uid is null then
    raise exception 'user with email % not found', p_email;
  end if;
  perform public.admin_link_member(p_account := p_account, p_user_uid := v_uid, p_role := p_role);
end;
$function$

CREATE OR REPLACE FUNCTION public.admin_register_owner(p_clinic_name text, p_owner_uid uuid)
 RETURNS uuid
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public', 'auth'
AS $function$
declare
  v_account_id uuid := gen_random_uuid();
begin
  if not public.is_super_admin() then
    raise exception 'forbidden' using errcode='P0001';
  end if;

  insert into public.accounts (id, name, owner_uid)
  values (v_account_id, p_clinic_name, p_owner_uid);

  insert into public.account_users (account_id, user_uid, role, disabled)
  values (v_account_id, p_owner_uid, 'owner', false)
  on conflict (account_id, user_uid)
  do update set role = excluded.role, disabled = false;

  return v_account_id;
end;
$function$

CREATE OR REPLACE FUNCTION public.admin_set_clinic_frozen(p_account_id uuid, p_frozen boolean)
