  cur_uid   uuid := auth.uid();
  v boolean := false;
BEGIN
  -- Ø¨Ø±ÙŠØ¯ Ø³ÙˆØ¨Ø± Ø§ÙØªØ±Ø§Ø¶ÙŠ
