  -- Ø¬Ø¯ÙˆÙ„ super_admins (Ø§Ø®ØªÙŠØ§Ø±ÙŠ/Ù…ØªØ¹Ø¯Ø¯ Ø§Ù„Ø­Ù‚ÙˆÙ„)
  IF EXISTS (SELECT 1 FROM information_schema.tables
             WHERE table_schema='public' AND table_name='super_admins') THEN
