CREATE OR REPLACE FUNCTION public.assign_employee_to_owner(employee_email text, owner_email text)
 RETURNS void
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
declare v_emp uuid; v_owner uuid; v_account uuid;
begin
  select id into v_emp from auth.users where email = employee_email;
  if v_emp is null then raise exception 'Employee email % not found in auth.users', employee_email; end if;

  select id into v_owner from auth.users where email = owner_email;
  if v_owner is null then raise exception 'Owner email % not found in auth.users', owner_email; end if;

  select account_id into v_account from public.profiles where id = v_owner;
  if v_account is null then raise exception 'Owner % has no profile/account yet. Call setup_owner first.', owner_email; end if;

  insert into public.profiles(id, role, account_id)
    values (v_emp, 'employee', v_account)
  on conflict (id) do update set role='employee', account_id=excluded.account_id;
end$function$

CREATE OR REPLACE FUNCTION public.audit_cloud_schema()
 RETURNS TABLE(table_name text, missing_cols text[], extra_cols text[])
 LANGUAGE plpgsql
AS $function$
declare
  rec record;
  actual text[];
  expected text[];
  t regclass;
begin
  for rec in
    select * from (values
      ('public.patients',           ARRAY['id','account_id','device_id','local_id','name','age','diagnosis','paid_amount','remaining','register_date','phone_number','health_status','preferences','doctor_id','doctor_name','doctor_specialization','notes','service_type','service_id','service_name','service_cost','doctor_share','doctor_input','tower_share','department_share']::text[]),
      ('public.returns',            ARRAY['id','account_id','device_id','local_id','date','patient_name','phone_number','diagnosis','remaining','age','doctor','notes']),
      ('public.consumptions',       ARRAY['id','account_id','device_id','local_id','patient_id','item_id','quantity','date','amount','note']),
      ('public.drugs',              ARRAY['id','account_id','device_id','local_id','name','notes','created_at']),
      ('public.prescriptions',      ARRAY['id','account_id','device_id','local_id','patient_id','doctor_id','record_date','created_at']),
      ('public.prescription_items', ARRAY['id','account_id','device_id','local_id','prescription_id','drug_id','days','times_per_day']),
      ('public.complaints',         ARRAY['id','account_id','device_id','local_id','title','description','status','created_at']),
      ('public.appointments',       ARRAY['id','account_id','device_id','local_id','patient_id','appointment_time','status','notes']),
      ('public.doctors',            ARRAY['id','account_id','device_id','local_id','employee_id','name','specialization','phone_number','start_time','end_time','print_counter']),
      ('public.consumption_types',  ARRAY['id','account_id','device_id','local_id','type']),
      ('public.medical_services',   ARRAY['id','account_id','device_id','local_id','name','cost','service_type']),
      ('public.service_doctor_share', ARRAY['id','account_id','device_id','local_id','service_id','doctor_id','share_percentage','tower_share_percentage','is_hidden']),
      ('public.employees',          ARRAY['id','account_id','device_id','local_id','name','identity_number','phone_number','job_title','address','marital_status','basic_salary','final_salary','is_doctor']),
      ('public.employees_loans',    ARRAY['id','account_id','device_id','local_id','employee_id','loan_date_time','final_salary','ratio_sum','loan_amount','leftover']),
      ('public.employees_salaries', ARRAY['id','account_id','device_id','local_id','employee_id','year','month','final_salary','ratio_sum','total_loans','net_pay','is_paid','payment_date']),
      ('public.employees_discounts',ARRAY['id','account_id','device_id','local_id','employee_id','discount_date_time','amount','notes']),
      ('public.item_types',         ARRAY['id','account_id','device_id','local_id','name']),
      ('public.items',              ARRAY['id','account_id','device_id','local_id','type_id','name','price','stock','created_at']),
      ('public.purchases',          ARRAY['id','account_id','device_id','local_id','item_id','quantity','total','created_at','date']),
      ('public.alert_settings',     ARRAY['id','account_id','device_id','local_id','item_id','threshold','is_enabled','last_triggered','created_at','notify_time']),
      ('public.financial_logs',     ARRAY['id','account_id','device_id','local_id','transaction_type','operation','amount','employee_id','description','modification_details','timestamp']),
      ('public.patient_services',   ARRAY['id','account_id','device_id','local_id','patient_id','service_id','service_name','service_cost'])
    ) as def(tbl, cols)
  loop
    -- Ù„Ùˆ Ø§Ù„Ø¬Ø¯ÙˆÙ„ ØºÙŠØ± Ù…ÙˆØ¬ÙˆØ¯: Ø£Ø¹ÙØ¯ ØµÙÙ‹Ø§ Ø¨ÙƒÙ„ Ø§Ù„Ø£Ø¹Ù…Ø¯Ø© ÙƒÙ€ "Ù†Ø§Ù‚ØµØ©"
    if to_regclass(rec.tbl) is null then
      table_name  := rec.tbl;
      missing_cols := rec.cols;
      extra_cols  := null;
      return next;
      continue;
    end if;

    t := to_regclass(rec.tbl);

    select array_agg(a.attname order by a.attname)
      into actual
    from pg_attribute a
    where a.attrelid = t
      and a.attnum > 0
      and not a.attisdropped;

    expected := rec.cols;

    select coalesce(array_agg(e), '{}')
      into missing_cols
    from unnest(expected) e
    where not (e = any(actual));

    select coalesce(array_agg(a), '{}')
      into extra_cols
    from unnest(actual) a
    where not (a = any(expected));

    if (missing_cols is not null and array_length(missing_cols,1) > 0)
       or (extra_cols is not null and array_length(extra_cols,1) > 0)
    then
      table_name := rec.tbl;
      return next;
    end if;
  end loop;
end
$function$

