END;
$function$

CREATE OR REPLACE FUNCTION public.fn_restore_conversation(p_conversation_id_text text)
 RETURNS TABLE(conv_restored integer, msg_restored integer, att_restored integer, reac_restored integer)
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
DECLARE
  v_conv text;
  v_acc  uuid;
BEGIN
  SELECT conv_id_text, account_id INTO v_conv, v_acc
  FROM public._resolve_conversation_and_account(p_conversation_id_text);

  IF v_conv IS NULL THEN
    RAISE NOTICE 'Conversation not found for id=%', p_conversation_id_text;
    conv_restored := 0; msg_restored := 0; att_restored := 0; reac_restored := 0;
    RETURN;
  END IF;

  IF NOT (
    EXISTS (SELECT 1 FROM public.account_users au
            WHERE au.account_id=v_acc AND au.user_uid=auth.uid()
              AND COALESCE(au.disabled,false)=false AND au.role IN ('owner'))
    OR EXISTS (SELECT 1 FROM public.super_admins s WHERE s.user_uid=auth.uid())
  ) THEN
    RAISE EXCEPTION 'Not allowed';
  END IF;

  UPDATE public.chat_reactions r
  SET is_deleted = FALSE, deleted_at = NULL
  FROM public.chat_messages m
  WHERE m.id = r.message_id
    AND m.conversation_id::text = v_conv
    AND COALESCE(r.is_deleted, FALSE) = TRUE;
  GET DIAGNOSTICS reac_restored = ROW_COUNT;

  UPDATE public.chat_attachments a
  SET is_deleted = FALSE, deleted_at = NULL
  FROM public.chat_messages m
  WHERE m.id = a.message_id
    AND m.conversation_id::text = v_conv
    AND COALESCE(a.is_deleted, FALSE) = TRUE;
  GET DIAGNOSTICS att_restored = ROW_COUNT;

  UPDATE public.chat_messages
  SET is_deleted = FALSE, deleted_at = NULL
  WHERE conversation_id::text = v_conv
    AND COALESCE(is_deleted, FALSE) = TRUE;
  GET DIAGNOSTICS msg_restored = ROW_COUNT;

  UPDATE public.chat_conversations
  SET is_deleted = FALSE, deleted_at = NULL
  WHERE id::text = v_conv
    AND COALESCE(is_deleted, FALSE) = TRUE;
  GET DIAGNOSTICS conv_restored = ROW_COUNT;

  RETURN;
END
$function$

