CREATE OR REPLACE FUNCTION public.fn_soft_delete_conversation(p_conversation_id_text text)
 RETURNS TABLE(conv_updated integer, msg_updated integer, att_updated integer, reac_updated integer)
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
DECLARE
  v_conv text;
  v_acc  uuid;
BEGIN
  SELECT conv_id_text, account_id INTO v_conv, v_acc
  FROM public._resolve_conversation_and_account(p_conversation_id_text);

  IF v_conv IS NULL THEN
    RAISE NOTICE 'Conversation not found for id=%', p_conversation_id_text;
    conv_updated := 0; msg_updated := 0; att_updated := 0; reac_updated := 0;
    RETURN;
  END IF;

  IF NOT (
    EXISTS (SELECT 1 FROM public.account_users au
            WHERE au.account_id=v_acc AND au.user_uid=auth.uid()
              AND COALESCE(au.disabled,false)=false AND au.role IN ('owner'))
    OR EXISTS (SELECT 1 FROM public.super_admins s WHERE s.user_uid=auth.uid())
  ) THEN
    RAISE EXCEPTION 'Not allowed';
  END IF;

  UPDATE public.chat_reactions r
    SET is_deleted = TRUE,
        deleted_at = timezone('utc', now()),
        updated_at = COALESCE(updated_at, timezone('utc', now()))
  FROM public.chat_messages m
  WHERE m.id = r.message_id
    AND m.conversation_id::text = v_conv
    AND COALESCE(r.is_deleted, FALSE) = FALSE;
  GET DIAGNOSTICS reac_updated = ROW_COUNT;

  UPDATE public.chat_attachments a
    SET is_deleted = TRUE,
        deleted_at = timezone('utc', now()),
        updated_at = COALESCE(updated_at, timezone('utc', now()))
  FROM public.chat_messages m
  WHERE m.id = a.message_id
    AND m.conversation_id::text = v_conv
    AND COALESCE(a.is_deleted, FALSE) = FALSE;
  GET DIAGNOSTICS att_updated = ROW_COUNT;

  UPDATE public.chat_messages
    SET is_deleted = TRUE,
        deleted_at = timezone('utc', now()),
        updated_at = COALESCE(updated_at, timezone('utc', now()))
  WHERE conversation_id::text = v_conv
    AND COALESCE(is_deleted, FALSE) = FALSE;
  GET DIAGNOSTICS msg_updated = ROW_COUNT;

  UPDATE public.chat_conversations
    SET is_deleted = TRUE,
        deleted_at = timezone('utc', now()),
        updated_at = COALESCE(updated_at, timezone('utc', now()))
  WHERE id::text = v_conv
    AND COALESCE(is_deleted, FALSE) = FALSE;
  GET DIAGNOSTICS conv_updated = ROW_COUNT;

  RETURN;
END
$function$

CREATE OR REPLACE FUNCTION public.rpc_start_dm(target_email text)
 RETURNS uuid
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
  _me uuid := auth.uid();
  _my_acc uuid;
  _is_super boolean := false;
  _target record;
  _conv uuid;
  _now timestamptz := NOW();
BEGIN
  IF _me IS NULL THEN
    RAISE EXCEPTION 'auth required';
  END IF;

  SELECT account_id, (lower(role)='superadmin') INTO _my_acc, _is_super
  FROM public.account_users
  WHERE user_uid = _me
  ORDER BY created_at DESC
  LIMIT 1;

  IF NOT _is_super THEN
    SELECT user_uid, email, account_id, role
      INTO _target
    FROM public.account_users
    WHERE account_id = _my_acc
      AND lower(email) = lower(target_email)
    LIMIT 1;
  ELSE
    SELECT user_uid, email, account_id, role
      INTO _target
    FROM public.account_users
    WHERE lower(email) = lower(target_email)
    LIMIT 1;
  END IF;

  IF NOT FOUND THEN
    RAISE EXCEPTION 'target not found';
  END IF;

  IF _target.user_uid = _me THEN
    RAISE EXCEPTION 'cannot DM yourself';
  END IF;

  -- Ø¥Ù† ÙˆÙØ¬Ø¯Øª Ù…Ø­Ø§Ø¯Ø«Ø© Ù…Ø¨Ø§Ø´Ø±Ø© Ù‚Ø§Ø¦Ù…Ø© Ø¨ÙŠÙ† Ø§Ù„Ø·Ø±ÙÙŠÙ†ØŒ Ø£Ø¹Ø¯Ù‡Ø§
  SELECT c.id
    INTO _conv
  FROM public.chat_conversations c
  JOIN public.chat_participants p1 ON p1.conversation_id = c.id AND p1.user_uid = _me
  JOIN public.chat_participants p2 ON p2.conversation_id = c.id AND p2.user_uid = _target.user_uid
  WHERE c.is_group = FALSE
  LIMIT 1;

  IF _conv IS NOT NULL THEN
    RETURN _conv;
  END IF;

  -- Ø£Ù†Ø´Ø¦ Ø§Ù„Ù…Ø­Ø§Ø¯Ø«Ø© + Ø³ÙŠÙØ¶Ø§Ù Ø§Ù„Ù…Ù†Ø´Ø¦ ÙƒÙ…Ø´Ø§Ø±Ùƒ ØªÙ„Ù‚Ø§Ø¦ÙŠØ§Ù‹ Ø¹Ø¨Ø± Ø§Ù„ØªØ±ÙŠØ¬Ø±
  INSERT INTO public.chat_conversations(id, account_id, is_group, title, created_by, created_at, updated_at)
  VALUES (gen_random_uuid(), coalesce(_my_acc, _target.account_id), FALSE, NULL, _me, _now, _now)
  RETURNING id INTO _conv;

  -- Ø£Ø¶Ù Ø§Ù„Ø·Ø±Ù Ø§Ù„Ø¢Ø®Ø± (upsert Ø§Ø­ØªØ±Ø§Ø²ÙŠ)
  INSERT INTO public.chat_participants (conversation_id, user_uid, email, joined_at)
  VALUES (_conv, _target.user_uid, lower(_target.email), _now)
  ON CONFLICT (conversation_id, user_uid) DO NOTHING;

  RETURN _conv;
END;
$function$

CREATE OR REPLACE FUNCTION public.setup_owner(clinic_name text, owner_email text)
 RETURNS uuid
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
declare v_owner uuid; v_account uuid;
begin
  select id into v_owner from auth.users where email = owner_email;
  if v_owner is null then raise exception 'Owner email % not found in auth.users', owner_email; end if;

  select account_id into v_account from public.profiles where id = v_owner limit 1;

  if v_account is null then
    insert into public.accounts(name) values (clinic_name) returning id into v_account;
    insert into public.profiles(id, role, account_id)
      values (v_owner, 'owner', v_account)
    on conflict (id) do update set role='owner', account_id=excluded.account_id;
  else
    update public.accounts set name = clinic_name where id = v_account;
    update public.profiles set role='owner' where id = v_owner;
  end if;
  return v_account;
end$function$

CREATE OR REPLACE FUNCTION public.setup_superadmin(super_email text)
 RETURNS uuid
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
declare v_user uuid; v_account uuid;
begin
  select id into v_user from auth.users where email = super_email;
  if v_user is null then raise exception 'Super admin email % not found in auth.users', super_email; end if;

  select id into v_account from public.accounts where name='Root Admin' limit 1;
  if v_account is null then
    insert into public.accounts(name) values ('Root Admin') returning id into v_account;
  end if;

  insert into public.profiles(id, role, account_id)
    values (v_user, 'superadmin', v_account)
  on conflict (id) do update set role='superadmin', account_id=excluded.account_id;

  return v_account;
end$function$

CREATE OR REPLACE FUNCTION public.setup_sync_table(p_table regclass)
 RETURNS void
 LANGUAGE plpgsql
AS $function$
    declare
      t_name  text := p_table::text;
      base    text := split_part(t_name, '.', 2);
      idx_name text := format('uix_%s_acc_dev_local', base);
    begin
      perform public._add_col_if_missing(p_table, 'account_id', 'uuid');
      perform public._add_col_if_missing(p_table, 'device_id',  'text');
      perform public._add_col_if_missing(p_table, 'local_id',   'bigint');

      if not exists (
        select 1 from pg_indexes
        where schemaname='public' and indexname=idx_name
      ) then
        execute format('create unique index %I on %s (account_id, device_id, local_id)', idx_name, t_name);
      end if;

      execute format('alter table %s enable row level security', t_name);

      begin execute format(
        'create policy %I on %s for select using (public.fn_is_account_member(account_id))',
        base||'_sel', t_name
      ); exception when duplicate_object then null; end;

      begin execute format(
        'create policy %I on %s for insert with check (public.fn_can_write_account(account_id))',
        base||'_ins', t_name
      ); exception when duplicate_object then null; end;

      begin execute format(
        'create policy %I on %s for update using (public.fn_can_write_account(account_id)) with check (public.fn_can_write_account(account_id))',
        base||'_upd', t_name
      ); exception when duplicate_object then null; end;

      begin execute format(
        'create policy %I on %s for delete using (public.fn_can_write_account(account_id))',
        base||'_del', t_name
      ); exception when duplicate_object then null; end;
    end;
    $function$

CREATE OR REPLACE FUNCTION public.tg__audit_log()
 RETURNS trigger
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
declare
  v_actor   uuid := auth.uid();
  v_email   text := current_setting('request.jwt.claim.email', true);
  v_before  jsonb;
  v_after   jsonb;
  v_diff    jsonb;
  v_pk      text;
  v_acc     uuid := coalesce( (to_jsonb(new)->>'account_id')::uuid
                            , (to_jsonb(old)->>'account_id')::uuid );
begin
  if TG_OP = 'INSERT' then
    v_after := to_jsonb(NEW);
  elsif TG_OP = 'UPDATE' then
    v_before := to_jsonb(OLD);
    v_after  := to_jsonb(NEW);
    -- Ø§Ø®ØªÙ„Ø§Ù Ø§Ù„Ø­Ù‚ÙˆÙ„
    v_diff := (
      select jsonb_object_agg(k, jsonb_build_object('from', v_before->k, 'to', v_after->k))
      from (
        select key as k
        from jsonb_each(v_after)
        where (v_before->key) is distinct from (v_after->key)
      ) s
    );
  else
    v_before := to_jsonb(OLD);
  end if;

  -- Ù…Ø­Ø§ÙˆÙ„Ø© Ø§Ø³ØªØ®Ø±Ø§Ø¬ Ù…ÙØªØ§Ø­ Ø§Ù„Ø³Ø·Ø± (id/local_id)
  v_pk := coalesce(
      to_jsonb(coalesce(NEW,OLD))->>'id',
      to_jsonb(coalesce(NEW,OLD))->>'local_id'
    );

  insert into public.audit_logs(account_id, actor_uid, actor_email, table_name, op, row_pk, before_row, after_row, diff, created_at)
  values (
    v_acc,
    v_actor,
    v_email,
    TG_TABLE_NAME,
    lower(TG_OP),
    v_pk,
    v_before,
    v_after,
    v_diff,
    now()
  );

  if TG_OP = 'DELETE' then
    return OLD;
  else
    return NEW;
  end if;
end $function$

